mapper.select:
SELECT
IFNULL(SUM(sales_count), 0) AS sales_count,
IFNULL(SUM(sales_price), 0) AS sales_price,
IFNULL(AVG(sales_avg_price),0) AS sales_avg_price
FROM
inventory_invoice_day
WHERE isvalid = 1

    AND market_id = #{marketId}


    AND report_time BETWEEN #{timeStart} and #{timeEnd}


    AND tenant_id = #{tenantId}

SELECT IFNULL((SUM(aa.sales_count < 10)),0) AS '10万以下', IFNULL((SUM(9 < aa.sales_count AND aa.sales_count < 20)),0) AS '10-20万', IFNULL((SUM(19 < aa.sales_count AND aa.sales_count < 30)),0) AS '20-30万', IFNULL((SUM(29 < aa.sales_count AND aa.sales_count < 40)),0) AS '30-40万', IFNULL((SUM(39 < aa.sales_count AND aa.sales_count < 50)),0) AS '40-50万以上', IFNULL((SUM(49 < aa.sales_count)),0) AS '50万以上' FROM (SELECT tenant_id, SUM(sales_count) AS sales_count FROM `inventory_invoice_day` WHERE isvalid = 1
AND market_id = #{marketId}
AND report_time BETWEEN #{timeStart} and #{timeEnd}
AND tenant_id = #{tenantId}
GROUP BY tenant_id) aa

SELECT
SUM(stock_count) AS stock_count,
SUM(stock_price) AS stock_price
FROM `inventory_invoice_day`
WHERE isvalid = 1

    AND market_id = #{marketId}


    AND tenant_id = #{tenantId}


    AND report_time BETWEEN #{timeStart} and #{timeEnd}

SELECT
IFNULL(AVG(stock_day_avg) , 0)
FROM
`inventory_invoice_day`
WHERE isvalid = 1

    AND market_id = #{marketId}


    AND tenant_id = #{tenantId}


    AND report_time BETWEEN #{timeStart} and #{timeEnd}

SELECT IFNULL(SUM(stock_count < 10),0) AS "10台以下", IFNULL(SUM(9 < stock_count AND stock_count < 20),0) AS "10-20台", IFNULL(SUM(19 < stock_count AND stock_count < 30),0) AS "20-30台", IFNULL(SUM(29 < stock_count AND stock_count < 40),0) AS "30-40台", IFNULL(SUM(39 < stock_count AND stock_count < 50),0) AS "40-50台", IFNULL(SUM(49 < stock_count ),0) AS "50台以上" FROM `carstocktime_day` WHERE isvalid = 1
AND market_id = #{marketId}
AND tenant_id = #{tenantId}
AND report_time = #{timeEnd}

SELECT SUM(stock_count < 10) AS '10台以下', SUM(9 < stock_count AND stock_count < 20) AS '10-20台', SUM(19 < stock_count AND stock_count < 30) AS '20-30台', SUM(29 < stock_count AND stock_count < 40) AS '30-40台', SUM(39 < stock_count AND stock_count < 50) AS '40-50台', SUM(49 < stock_count) AS '50台以上' FROM `inventory_invoice_day` WHERE isvalid = 1
AND market_id = #{marketId}
AND tenant_id = #{tenantId}
AND report_time = #{timeEnd}

SELECT
COUNT(*) AS `count`
FROM
`carstock_day`
WHERE isvalid = 1

    AND market_id = #{marketId}


    AND tenant_id = #{tenantId}


    AND report_time = #{timeEnd}


    AND invoice_stock_id = #{carNumType}
